mapper.insert:
INSERT INTO  /* AtrzMapper.insertAtrzLine */
	atrz_line 
	(
	  atrz_doc_no
	, atrz_ln_sn
	, sanctner_empno
	, atrz_ty
	, sanctn_progrs_sttus_code
	, dcrb_author_yn
	)
VALUES
	(
	#{atrzDocNo}
	, #{atrzLnSn}
	, #{sanctnerEmpno}
	, #{atrzTy}
	, '00'
	, #{dcrbAuthorYn}
	)


	SELECT NVL(MAX(HOLI_ACTPLN_NO),0)+1 FROM DOCUM_HOLIDAY

INSERT INTO /* AtrzMapper.insertHoliday */
	docum_holiday    
	(
	  HOLI_ACTPLN_NO
	, ATRZ_DOC_NO
	, HOLI_CODE
	, HOLI_START
	, HOLI_END
	)
VALUES
	(
	  #{holiActplnNo}
	, #{atrzDocNo}
	, #{holiCode}
	, #{holiStart}
	, #{holiEnd}
	)

mapper.select:
SELECT /* DOCUM_HOLIDAY (연차신청서) */ HO.holi_actpln_no ,HO.atrz_doc_no ,HO.holi_code ,HO.holi_start ,HO.holi_end
/* ATRZ (전자결재 기본 정보) */ ,A.ATRZ_DOC_NO AS A_DOC_NO ,A.DRAFTER_EMPNO ,A.DRAFTER_CLSF ,A.DRAFTER_EMPNM ,A.DRAFTER_DEPT ,A.BKMK_YN ,A.ATCH_FILE_NO ,A.ATRZ_SJ ,A.ATRZ_CN ,A.ATRZ_OPINION ,A.ATRZ_TMPR_STRE_DT ,A.ATRZ_DRFT_DT ,A.ATRZ_COMPT_DT ,A.ATRZ_RTRVL_DT ,A.ATRZ_STTUS_CODE ,A.ELTSGN_IMAGE AS DRAFTER_ELTSGN ,A.DOC_FORM_NO ,A.ATRZ_DELETE_YN ,A.SCHDUL_REG_YN
/* ATRZ_LINE (결재선) */ ,L.ATRZ_LN_SN ,L.SANCTNER_EMPNO ,L.SANCTNER_CLSF_CODE ,L.CONTD_EMPNO ,L.CONTD_CLSF_CODE ,L.DCRB_MAN_EMPNO ,L.DCRB_MAN_CLSF_CODE ,L.ATRZ_TY ,L.SANCTN_PROGRS_STTUS_CODE ,L.DCRB_AUTHOR_YN ,L.CONTD_AUTHOR_YN ,L.SANCTN_OPINION ,L.ELTSGN_IMAGE AS SANCTNER_ELTSGN ,L.SANCTN_CONFM_DT
FROM DOCUM_HOLIDAY HO LEFT JOIN ATRZ A ON HO.ATRZ_DOC_NO = A.ATRZ_DOC_NO LEFT JOIN ATRZ_LINE L ON HO.ATRZ_DOC_NO = L.ATRZ_DOC_NO WHERE HO.HOLI_ACTPLN_NO = #{holiActplnNo} ORDER BY L.ATRZ_LN_SN
