mapper.update:
UPDATE CHAT_EMP
SET LAST_CNFIRM_NO = #{mssageSn}
WHERE CHTT_ROOM_NO = #{chttRoomNo}
AND EMPL_NO = #{emplNo}

UPDATE CHAT_ROOM
SET LAST_MSSAGE_NO = #{mssageSn}
WHERE CHTT_ROOM_NO = #{chttRoomNo}

mapper.insert:

    SELECT NVL(MAX(MSSAGE_SN), 0) + 1 FROM MESSAGE
    WHERE CHTT_ROOM_NO = #{chttRoomNo}


INSERT INTO MESSAGE(
    MSSAGE_SN, EMPL_NO, CHTT_ROOM_NO, MSSAGE_CN, MSSAGE_TY, CREAT_DE
)
VALUES (
    #{mssageSn}, #{emplNo}, #{chttRoomNo}, #{mssageCn}, #{mssageTy}, #{creatDe}
)

mapper.select:
SELECT EMPL_NO
FROM CHAT_EMP
WHERE CHTT_ROOM_NO = #{chttRoomNo}
AND EMPL_NO != #{emplNo}
